-- ----------------------------
-- Function structure for after_sync
-- ----------------------------
DROP FUNCTION IF EXISTS "stategrid"."after_sync"();
CREATE OR REPLACE FUNCTION "stategrid"."after_sync"()
  RETURNS "pg_catalog"."void" AS $BODY$
	BEGIN
	
	-- 初始化admin
	insert into "stategrid"."t_sec_user"("f_pid", "f_name", "f_code", "f_password", "f_sys_flag") values ('1001', 'admin', 'admin', 'a2720eff0ba78bd7722ea504bf740b36', '1');
	
	-- 部门初始化为浙江省公司
	insert into t_user_info("f_pid", "f_user_name", "f_realname", "f_password", "f_org_id", "f_group_org_id", "f_sys_flag") values ('1001', 'admin', 'admin', 'a2720eff0ba78bd7722ea504bf740b36', '8a812897493378a001495677ad086663', '8a812897493378a001495677ad086663', '1');
	
	-- 初始化角色的部门路径
	update t_sec_role set f_route = '8a812897493378a0014955ddce5f5937,8a812897493378a0014956770fd56660,8a812897493378a001495677ad086663';

	
	-- 生成部门树
	with recursive tmp as(
		select 1 as level, f_pid, f_parent_id, f_pid::text as route from t_organization where f_parent_id is null
		union all
		select tmp.level + 1, t.f_pid, t.f_parent_id, concat(tmp.route, ',', t.f_pid)::text as route from t_organization t inner join tmp on tmp.f_pid = t.f_parent_id
	)
	update t_organization org set f_route = tmp.route, f_org_level = tmp.level from tmp where org.f_pid = tmp.f_pid;
	
	-- 设备生成部门树
	update t_equ_dic equ set f_org_route = (select f_route from t_organization org where org.f_pid = coalesce(equ.f_other_pid3, equ.f_other_pid2, equ.f_other_pid1)), f_status = '1';
	
	-- 删除非运维检修部的人员
	delete from t_user_info where f_group_org_id not in (select f_pid from t_organization);
	delete from t_sec_user where f_pid not in (select f_pid from t_user_info);
	
	-- 清理并生成用户组织关系
	truncate t_org_user;

	insert into t_org_user select uuid_generate_v4(), f_group_org_id, f_pid, '1', '1', to_char(current_timestamp, 'yyyyMMddHH24MISS'), null, 1 from t_user_info where f_group_org_id is not null;
	
END
$BODY$
  LANGUAGE plpgsql VOLATILE
  COST 100;

-- ----------------------------
-- Function structure for clean_equ_data
-- ----------------------------
DROP FUNCTION IF EXISTS "stategrid"."clean_equ_data"("org_id" text);
CREATE OR REPLACE FUNCTION "stategrid"."clean_equ_data"("org_id" text)
  RETURNS "pg_catalog"."void" AS $BODY$
DECLARE

  ICOUNT bigint;

BEGIN
    --设备检修周期
    delete from t_cbm_equcycle ey
    where ey.f_equ_pid in (SELECT d.f_pid
              from t_equ_dic d, t_organization org
             where d.f_classification = '1'
               and d.f_other_pid2 = org.f_pid
               and org.f_route like CONCAT(CONCAT('%', ORG_ID), '%'));
    --变电站班组关系表
    delete
      from t_equ_dataauthority t
     where t.f_substation_pid in (SELECT d.f_pid
              from t_equ_dic d, t_organization org
             where d.f_classification = '3'
               and d.f_other_pid2 = org.f_pid
               and org.f_route like CONCAT(CONCAT('%', ORG_ID), '%'));

    --变更明细表
    delete
      from t_equ_dic_change_detail c
     where c.f_parent_pid in (SELECT ch.f_pid
              from t_equ_dic_change ch
             where ch.f_dic_pid in (select d.f_pid
                      from t_equ_dic d, t_organization org
                     where d.f_other_pid2 = org.f_pid
                       and org.f_route like CONCAT(CONCAT('%', ORG_ID), '%')));
    --变更表
    delete
      from t_equ_dic_change ch
     where ch.f_dic_pid in (SELECT d.f_pid
              from t_equ_dic d, t_organization org
             where d.f_other_pid2 = org.f_pid
               and org.f_route like CONCAT(CONCAT('%', ORG_ID), '%'));
    --设备表
    --1、间隔
    delete
      from t_equ_dic t
     where t.f_classification = '4'
       and t.f_other_pid9 in (SELECT d.f_pid
              from t_equ_dic d, t_organization org
             where d.f_classification = '3'
               and d.f_other_pid2 = org.f_pid
               and org.f_route like CONCAT(CONCAT('%', ORG_ID), '%'));
    --2、组部件
    delete
      from t_equ_dic t
     where t.f_classification = '2'
       and t.f_other_pid9 in (SELECT d.f_pid
              from t_equ_dic d, t_organization org
             where d.f_classification = '1'
               and d.f_other_pid2 = org.f_pid
               and org.f_route like CONCAT(CONCAT('%', ORG_ID), '%'));
    --3、设备、变电站、备品、备件、工器具、仓库
    delete
    from t_equ_dic t
    where t.f_pid in (
    SELECT d.f_pid
              from t_equ_dic d, t_organization org
             where d.f_other_pid2 = org.f_pid
		           and org.f_route like CONCAT(CONCAT('%', ORG_ID), '%'));
  COMMIT;

END;
$BODY$
  LANGUAGE plpgsql VOLATILE
  COST 100;

