-- ----------------------------
-- Function structure for decode
-- ----------------------------
DROP FUNCTION IF EXISTS "stategrid"."decode"(VARIADIC "args" anyarray);
CREATE OR REPLACE FUNCTION "stategrid"."decode"(VARIADIC "args" anyarray)
  RETURNS "pg_catalog"."anyelement" AS $BODY$DECLARE
	length int2 = array_length(args, 1);
	index int2 = 2;
BEGIN
	-- 参数列表不允许低于数量3
	if 
		length < 3 
	then 
		raise exception '参数数量不能低于3';
	end if;
	
	-- 索引从2开始, 跳过被比较值
	while index < length
	loop
		if 
			-- 比较值与被比较值相等, 即返回数组中下一元素
			args[index] = args[1]
		then 
			return args[index + 1];
		end if;
		index = index + 2;
	end loop;
	
	if 
		length % 2 = 0
	then 
		-- 没有匹配, 返回默认值
		return args[length];
	end if;

	-- 同Oracle, 没有默认值返回为null
	return null;
end;

$BODY$
  LANGUAGE plpgsql VOLATILE
  COST 100;

