DROP FUNCTION IF EXISTS "stategrid"."f_format_number"("execnum" int8);
CREATE OR REPLACE FUNCTION "stategrid"."f_format_number"("execnum" int8)
  RETURNS "pg_catalog"."varchar" AS $BODY$
DECLARE

  rst varchar(60);

BEGIN
  if (execnum is not null) then
  rst := CASE
         WHEN execnum - TRUNC(execnum) = 0 THEN
          execnum::varchar
         ELSE
          to_char(execnum,'fm9999999990.00') END;
  end if;

  return rst;
  end;


 

$BODY$
  LANGUAGE plpgsql STABLE
  COST 100;

