DROP FUNCTION IF EXISTS "stategrid"."f_get_week_begin"();
CREATE OR REPLACE FUNCTION "stategrid"."f_get_week_begin"()
  RETURNS "pg_catalog"."varchar" AS $BODY$
DECLARE

  FunctionResult varchar(8);

BEGIN
  SELECT to_char(CASE (to_char(clock_timestamp(), 'D'))::numeric
           WHEN 7 THEN
            clock_timestamp() - interval '5 days'
           WHEN 6 THEN
            clock_timestamp() - interval '4 days'
           WHEN 5 THEN
            clock_timestamp() - interval '3 days'
           WHEN 4 THEN
            clock_timestamp() - interval '2 days'
           WHEN 3 THEN
            clock_timestamp() - interval '1 days'
           WHEN 2 THEN
            clock_timestamp()
           else
            clock_timestamp() - interval '6 days'
         END,'yyyymmdd')
    into STRICT FunctionResult
;
  return(FunctionResult);
end;


 

$BODY$
  LANGUAGE plpgsql STABLE
  COST 100;

