-- ----------------------------
-- Function structure for instr
-- ----------------------------
DROP FUNCTION IF EXISTS "stategrid"."instr"("str1" varchar, "str2" varchar, "begin_index" int4, "since_count" int4);
CREATE OR REPLACE FUNCTION "stategrid"."instr"("str1" varchar, "str2" varchar, "begin_index" int4, "since_count" int4)
  RETURNS "pg_catalog"."int4" AS $BODY$
	declare
		length int4 = char_length(str1);
		index int4 = 0;
		count int4 = 0;
		total int4;
		target varchar;
BEGIN
	-- 处理字符串反转
	if
		begin_index >= 0
	then
		total = begin_index;
	else
		total = -begin_index;
		str1 = reverse(str1);
	end if;
	
	target = substring(str1, total);
	
	while index <= length
	loop
		index = position(str2 in target);
		total = total + index;
		target = substring(str1, total);
		if 
			index = 0
		then
			return 0;
		else
			count = count + 1;
			if 
				count = since_count
			then
				if
					begin_index >= 0
				then 
					return total - 1;
				else 
					return length + 1 - (total - 1);
				end if;
			end if;
		end if;
	end loop;
end;
$BODY$
  LANGUAGE plpgsql VOLATILE
  COST 100;

