  RETURNS "pg_catalog"."void" AS $BODY$
DECLARE

  row_data CURSOR FOR
  SELECT ttt.f_pid  from T_DETECT_REPORT_DETAIL ttt
   where ttt.F_REPORT_PID in (SELECT tt.f_pid
            from T_DETECT_REPORT tt
           where tt.f_plan_pid in (select t.f_pid
                    from T_DETECT_PLAN t
                   where t.f_org_route like CONCAT(CONCAT('%', ORG_ID), '%') and t.F_BUSINESSTYPE= BUSINESSTYPE));
  /*
  根据组织机构和检测类型删除检测数据。
  检测报告中的检测数据是由100多张帆远模板中的，这个数据分布在100多张表中，其中T_DETECT_REPORT_DETAIL的f_tablename字段
  记录了保存在哪些表中（如果有多张表，此字段使用逗号分隔。）所以删除的表有可能是多个动态的。
  */
BEGIN
    for item in row_data loop
      PERFORM p_delete_report_data1(item.f_pid);
    end loop;
  end;
