-- Function structure for p_update_equ_route
-- ----------------------------
DROP FUNCTION IF EXISTS "stategrid"."p_update_equ_route"("equpid" text);
CREATE OR REPLACE FUNCTION "stategrid"."p_update_equ_route"("equpid" text)
  RETURNS "pg_catalog"."void" AS $BODY$
DECLARE

--获取变电站，当传入设备ID参数为空时，取所有变电站
  cur_equdic CURSOR FOR
    SELECT *
      from t_equ_dic a
     where a.f_sys_flag = '1'
       and a.f_base_pid = '2f3d1877716b412e9084e40a11c8bc76'
       and a.f_pid like '%' || equPid || '%';
  route varchar(4000);
BEGIN

  for r_equdic in cur_equdic loop
    begin
    --获取变电站运维班组、关联班组的路由
      select string_agg(column_value,',')
      into route
        from (SELECT distinct *
                from (
                select regexp_split_to_table(t1.f_route,',') column_value
                              from (select a.f_workteam
                                      from t_equ_dataauthority a
                                     where a.f_sys_flag = '1'
                                       and a.f_substation_pid = r_equdic.f_pid

				   union all

                                    SELECT a.f_other_pid3 as f_workteam
                                      from t_equ_dic a
                                     where a.f_sys_flag = '1'
                                       and a.f_pid = r_equdic.f_pid) t,
                                   t_organization t1
                             where t.f_workteam = t1.f_pid) alias4) alias5;
                            -- dbms_output.put_line(route);
    --更新变电站路由 
     update t_equ_dic
         set f_org_route = route
       where f_pid = r_equdic.f_pid;
    --更新该变电站下所有设备路由
      update t_equ_dic
         set f_org_route = route
       where f_other_pid8 = r_equdic.f_pid;
    end;

  end loop;

end;
$BODY$
  LANGUAGE plpgsql VOLATILE
  COST 100;

