DROP FUNCTION IF EXISTS "stategrid"."pro_test"();
CREATE OR REPLACE FUNCTION "stategrid"."pro_test"()
  RETURNS "pg_catalog"."void" AS $BODY$
DECLARE

v_f_pid varchar(32);
v_f_attrcolumn varchar(32);--字段名
tempsql varchar(2000);
cur_tmp CURSOR FOR SELECT b.f_pid from t_equ_base b where b.f_parent_pid = '9cd98c2711fc4f2f9c561b126fbb5af6' and b.f_sys_flag = '1';
cur_tmp1 CURSOR(param  text) FOR SELECT * from t_equ_base_attr t where t.F_BASE_PID in (WITH RECURSIVE cte AS (
select  f_pid from T_EQU_BASE t WHERE f_pid =param
  UNION ALL
select  f_pid from T_EQU_BASE t JOIN cte c ON (c.f_parent_pid = t.f_pid)

) SELECT * FROM cte
) and t.f_sys_flag='1' and t.f_attr_datatype='1008';
BEGIN
 for cur_row in cur_tmp loop
       v_f_pid := cur_row.f_pid;
       for cur_row1 in cur_tmp1(v_f_pid)

        loop
         
           v_f_attrcolumn := 'f_'||cur_row1.f_attr_column;
           --if v_f_datatype = '1008' then
           RAISE NOTICE '%', v_f_attrcolumn;

           tempsql :='update t_equ_dic1 set '||v_f_attrcolumn||'= substr('||v_f_attrcolumn||',0,10) where '||v_f_attrcolumn||' is not null and f_base_pid=:1';
           RAISE NOTICE '%', tempsql;
           EXECUTE tempsql using  v_f_pid;
           commit;
           --end if;
        end loop;
   end loop;
   exception when others then
      begin
        rollback;
      end;
end;
$BODY$
  LANGUAGE plpgsql VOLATILE
  COST 100;

