DROP FUNCTION IF EXISTS "stategrid"."truncate_all_table"("p_schemaname" varchar);
CREATE OR REPLACE FUNCTION "stategrid"."truncate_all_table"("p_schemaname" varchar)
  RETURNS "pg_catalog"."void" AS $BODY$
DECLARE
    tmp VARCHAR(512);
DECLARE names CURSOR FOR 
    select tablename from pg_tables where schemaname=p_schemaname;
BEGIN
  FOR stmt IN names LOOP
    tmp := 'TRUNCATE TABLE '|| quote_ident(stmt.tablename) || ' CASCADE;';
RAISE NOTICE 'notice: %', tmp;
    EXECUTE 'TRUNCATE TABLE '|| quote_ident(stmt.tablename) || ' CASCADE;';
  END LOOP;
  RAISE NOTICE 'finished .....';
END;


$BODY$
  LANGUAGE plpgsql VOLATILE
  COST 100;

