-- ----------------------------
-- View structure for cbm_card_view
-- ----------------------------
DROP VIEW IF EXISTS "stategrid"."cbm_card_view";
CREATE VIEW "stategrid"."cbm_card_view" AS  SELECT t.f_plan_pid,
    string_agg((t.f_equ_pid)::text, ','::text ORDER BY (t.f_equ_pid)::text) AS equids
   FROM ( SELECT t_1.f_plan_pid,
            t_1.f_equ_pid
           FROM t_cbm_card t_1
          WHERE ((t_1.f_sys_flag = '1'::bpchar) AND ((t_1.f_type)::text = '2'::text))
          GROUP BY t_1.f_plan_pid, t_1.f_equ_pid) t
  GROUP BY t.f_plan_pid;

-- ----------------------------
-- View structure for cbm_plan_view
-- ----------------------------
DROP VIEW IF EXISTS "stategrid"."cbm_plan_view";
CREATE VIEW "stategrid"."cbm_plan_view" AS  SELECT p.f_pid,
    p.f_planname,
    string_agg((d.f_equ_pid)::text, ','::text ORDER BY (d.f_equ_pid)::text) AS equids
   FROM ((t_cbm_plan p
     LEFT JOIN t_cbm_task t ON (((p.f_pid)::text = (t.f_plan_pid)::text)))
     LEFT JOIN t_cbm_taskdetail d ON (((t.f_pid)::text = (d.f_task_pid)::text)))
  WHERE (((p.f_sys_flag = '1'::bpchar) AND (t.f_sys_flag = '1'::bpchar)) AND (d.f_sys_flag = '1'::bpchar))
  GROUP BY p.f_pid, p.f_planname;

-- ----------------------------
-- View structure for cbm_card_plan_view
-- ----------------------------
DROP VIEW IF EXISTS "stategrid"."cbm_card_plan_view";
CREATE VIEW "stategrid"."cbm_card_plan_view" AS  SELECT p.f_pid,
    p.f_province,
    p.f_city,
    p.f_branch,
    p.f_workteam,
    p.f_org_route,
    p.f_year,
    p.f_month,
    p.f_week,
    p.f_substation_pid,
    p.f_planname,
    p.f_workcontent,
    e.f_planstartdate,
    e.f_planenddate,
    p.f_editor_pid,
    p.f_editdate,
    p.f_localtype_pid,
    p.f_plantype_pid,
    p.f_status_pid,
    p.f_flowstatus_pid,
    p.f_remark,
    p.f_sys_flag,
    p.f_creator,
    p.f_create_time,
    p.f_last_modifier,
    p.f_last_modified_time,
    p.f_value1,
    p.f_value2,
    p.f_value3,
    p.f_value4,
    p.f_value5,
    p.f_equname_content,
    p.f_equcode_content,
    p.f_intervalname_content,
    p.f_classify_content,
    p.f_taskcource_content,
    p.f_plancode,
    p.f_flow_state,
    p.f_flow_task_name,
    p.f_taskcource_pid,
    p.f_workteamname,
    p.f_inchargename,
    p.f_workteammember,
    t4.f_dic_name AS branch,
    t5.f_dic_name AS workteam,
    b.f_dic_name AS substationname,
    p.f_planname AS planname,
    p.f_plancode AS plancode,
    e.f_planstartdate AS planstartdate,
    e.f_planenddate AS planenddate,
    e.f_planstartdate AS plantime,
    t1.f_dic_name AS localtypename,
    p.f_equname_content AS equname,
    p.f_equname_content AS equnamecontent,
    p.f_equcode_content AS equcode,
    p.f_workcontent AS workcontent,
    p.f_branch AS branch_id,
    p.f_workteam AS workteam_id
   FROM ((((((t_cbm_plan p
     LEFT JOIN t_equ_dic b ON (((p.f_substation_pid)::text = (b.f_pid)::text)))
     LEFT JOIN t_pub_dic t1 ON (((p.f_localtype_pid)::text = (t1.f_pid)::text)))
     LEFT JOIN t_pub_dic t4 ON (((p.f_branch)::text = (t4.f_pid)::text)))
     LEFT JOIN t_pub_dic t5 ON (((p.f_workteam)::text = (t5.f_pid)::text)))
     LEFT JOIN t_cbm_planexec e ON (((((e.f_plan_pid)::text = (p.f_pid)::text) AND (e.f_sys_flag = '1'::bpchar)) AND ((e.f_step)::text = '9'::text))))
     JOIN ( SELECT b_1.f_pid
           FROM cbm_card_view a,
