DROP VIEW IF EXISTS "stategrid"."v_levt_rule_dic";
CREATE VIEW "stategrid"."v_levt_rule_dic" AS  SELECT tt.f_equ_id,
    tt.f_equ_name,
    tt.f_order,
    tt.f_equ_type_id,
    tt.f_rule_type,
    tt.f_type
   FROM ( SELECT r.f_equbase_pid AS f_equ_type_id,
            b.f_pid AS f_equ_id,
            b.f_base_name AS f_equ_name,
            b.f_order,
            '1'::bpchar AS f_rule_type,
            '0'::bpchar AS f_type,
            1 AS order_no
           FROM t_equ_base b,
            t_equ_relation r
          WHERE ((((r.f_componentbase_pid)::text = (b.f_pid)::text) AND (r.f_sys_flag = '1'::bpchar)) AND (b.f_sys_flag = '1'::bpchar))
        UNION
         SELECT t1.f_equ_type_pid AS f_equ_type_id,
            t1.f_pid AS f_equ_id,
            t1.f_dic_name AS f_equ_name,
            t1.f_order_no AS f_order,
            t1.f_rule_type,
            t1.f_type,
            2 AS order_no
           FROM t_levt_rule_type_dic t1
          WHERE (t1.f_sys_flag = '1'::bpchar)) tt
  ORDER BY tt.order_no, tt.f_order;

