-- View structure for v_levt_equ_program
-- ----------------------------
DROP VIEW IF EXISTS "stategrid"."v_levt_equ_program";
CREATE VIEW "stategrid"."v_levt_equ_program" AS  SELECT lp.f_pid AS f_parent_id,
    t3.f_levt_year,
    t3.f_province,
    t3.f_city,
    t3.f_branch,
    t3.f_workteam,
    t3.f_voltage_classes,
    t3.f_substation_id,
    ed.f_value1 AS substation_start_time,
    t3.f_finish_date AS check_date,
    t3.f_equ_id AS equ_id,
    d1.f_dic_name AS equ_name,
    d1.f_dic_code AS equ_code,
    t2.f_unit_id AS unit_id,
    v1.f_equ_name AS unit_name,
    t1.f_project,
    t1.f_child_project,
    t1.f_problem_remark,
    t3.f_levt_type,
        CASE
            WHEN (t3.f_levt_type = '1'::bpchar) THEN '自评'::text
            WHEN (t3.f_levt_type = '2'::bpchar) THEN '抽评'::text
            ELSE NULL::text
        END AS levt_type_name,
    plf.f_pid,
    plf.f_problem_pid,
    plf.f_is_danger,
    plf.f_is_stop,
    plf.f_fix_user,
    plf.f_fix_time,
    plf.f_fix_remark,
        CASE
            WHEN (plf.f_is_finish IS NULL) THEN '0'::bpchar
            ELSE plf.f_is_finish
        END AS f_is_finish,
    plf.f_sys_flag,
    plf.f_creator,
    plf.f_create_time,
    plf.f_last_modifier,
    plf.f_last_modified_time,
        CASE
            WHEN (lpc.f_complain_flag IS NULL) THEN '0'::bpchar
            ELSE lpc.f_complain_flag
        END AS levt_sys_flag,
    t3.f_levt_user AS levt_user,
    lp.f_levt_time AS levt_create_time,
    lp.f_problem_resource,
    t1.f_deduct_point AS deduct_point,
    d1.f_base_pid AS equ_pid,
    t1.f_file AS file_id
   FROM ((((((((t_levt_problem lp
     LEFT JOIN t_levt_problem_fix plf ON ((((lp.f_pid)::text = (plf.f_problem_pid)::text) AND (plf.f_sys_flag = '1'::bpchar))))
     LEFT JOIN t_levt_problem_complain lpc ON ((((lpc.f_problem_pid)::text = (lp.f_pid)::text) AND (lpc.f_sys_flag = '1'::bpchar))))
     JOIN t_levt_child_project t1 ON ((((t1.f_pid)::text = (lp.f_parent_pid)::text) AND (t1.f_sys_flag = '1'::bpchar))))
     LEFT JOIN t_levt_equ_unit t2 ON ((((t1.f_equ_unit_pid)::text = (t2.f_pid)::text) AND (t2.f_sys_flag = '1'::bpchar))))
     LEFT JOIN t_levt_equ t3 ON ((((t2.f_equ_pid)::text = (t3.f_pid)::text) AND (t3.f_sys_flag = '1'::bpchar))))
     LEFT JOIN t_equ_dic d1 ON (((t3.f_equ_id)::text = (d1.f_pid)::text)))
     LEFT JOIN v_levt_rule_dic v1 ON ((((v1.f_equ_id)::text = (t2.f_unit_id)::text) AND ((v1.f_equ_type_id)::text = (d1.f_base_pid)::text))))
     LEFT JOIN t_equ_dic ed ON (((ed.f_sys_flag = '1'::bpchar) AND ((ed.f_pid)::text = (t3.f_substation_id)::text))))
  WHERE ((lp.f_sys_flag = '1'::bpchar) AND (lp.f_problem_resource = '1'::bpchar))
