    le.f_workteam,
    le.f_voltage_classes,
    le.f_substation_id,
    ed.f_value1 AS substation_start_time,
    le.f_finish_date AS check_date,
    equdic.f_pid AS equ_id,
    equdic.f_dic_name AS equ_name,
