     LEFT JOIN t_levt_problem_complain lpc ON ((((lpc.f_problem_pid)::text = (lp.f_pid)::text) AND (lpc.f_sys_flag = '1'::bpchar))))
     JOIN t_levt_equ le ON ((((le.f_pid)::text = (lp.f_parent_pid)::text) AND (le.f_sys_flag = '1'::bpchar))))
     LEFT JOIN t_equ_dic ed ON (((ed.f_sys_flag = '1'::bpchar) AND ((ed.f_pid)::text = (le.f_substation_id)::text))))
     LEFT JOIN t_equ_dic equdic ON ((((equdic.f_pid)::text = (le.f_equ_id)::text) AND (equdic.f_sys_flag = '1'::bpchar))))
