   FROM (((t_levt_expert t
     LEFT JOIN t_user_strong_point st ON (((t.f_user_id)::text = (st.f_user_id)::text)))
     LEFT JOIN ( SELECT alias2.f_order,
            alias2.f_equ_sum,
            alias2.f_bonus_point,
            alias2.f_get_point,
            alias2.f_problem_sum,
            alias2.f_valid_problem_sum,
            alias2.f_parent_pid,
            alias2.f_skill_id
           FROM ( SELECT rank() OVER (PARTITION BY ((((tte.f_expert_level)::text || (tte.f_levt_year)::text) || (tt2.f_parent_pid)::text) || (tt2.f_skill_id)::text) ORDER BY tt2.f_create_time DESC) AS f_order,
                    tt2.f_equ_sum,
                    tt2.f_bonus_point,
                    tt2.f_get_point,
                    tt2.f_problem_sum,
                    tt2.f_valid_problem_sum,
                    tt2.f_parent_pid,
                    tt2.f_skill_id
                   FROM t_levt_skill_expert tt2,
                    t_levt_expert tte
                  WHERE ((((tt2.f_parent_pid)::text = (tte.f_pid)::text) AND (tte.f_sys_flag = '1'::bpchar)) AND (tt2.f_sys_flag = '1'::bpchar))) alias2
          WHERE (alias2.f_order = 1)) t2 ON ((((t.f_pid)::text = (t2.f_parent_pid)::text) AND ((st.f_strong_point)::text = (t2.f_skill_id)::text))))
     LEFT JOIN ( SELECT tet.f_substation_sum,
