            tet.f_skill_id,
            tet.f_valid_problem_sum
           FROM t_levt_skill_expert_total tet
          WHERE (tet.f_sys_flag = '1'::bpchar)) t3 ON ((((t3.f_parent_pid)::text = (t.f_pid)::text) AND ((t3.f_skill_id)::text = (t2.f_skill_id)::text))))
