DROP VIEW IF EXISTS "stategrid"."v_levt_expert_order";
CREATE VIEW "stategrid"."v_levt_expert_order" AS  SELECT t.f_user_id,
    t.f_levt_year,
    e.f_belong_org_id AS belong_org_id,
    e.f_yw_org_id AS yw_org_id,
    e.f_group_org_id AS org_id,
    e.f_realname AS user_name,
        CASE
            WHEN (e.f_sex = '1'::bpchar) THEN '男'::text
            ELSE '女'::text
        END AS user_sex_name,
    f_get_dic_name((e.f_post)::text) AS post_name,
    floor((date_part('epoch'::text, (now() - to_timestamp('20170428'::text, 'yyyymmdd'::text))) / (((365 * 24) * 3600))::double precision)) AS work_years,
    f.f_major_id AS major_id,
    f.f_major_name AS major_name,
    t2.f_substation_id AS substation_id,
    t2.f_voltage_classes AS voltage_classes,
    t2.f_levt_type,
    COALESCE(sg.f_get_point, (0)::numeric) AS get_point,
    sg.f_problem_sum AS problem_sum,
    sg.f_equ_sum AS equ_num,
    '0'::text AS is_yj
   FROM (((((t_levt_expert t
     JOIN t_user_info e ON ((((t.f_user_id)::text = (e.f_pid)::text) AND (e.f_sys_flag = '1'::bpchar))))
     JOIN t_levt_task_expert t1 ON ((((t1.f_user_id)::text = (t.f_user_id)::text) AND (t1.f_sys_flag = '1'::bpchar))))
     JOIN t_levt_equ_task t2 ON ((((t1.f_cp_equ_levt_task_pid)::text = (t2.f_pid)::text) AND (t2.f_sys_flag = '1'::bpchar))))
     LEFT JOIN t_user_small_major f ON ((((t.f_user_id)::text = (f.f_user_id)::text) AND (f.f_sys_flag = '1'::bpchar))))
     LEFT JOIN ( SELECT ex.f_parent_pid,
            sum(ex.f_equ_sum) AS f_equ_sum,
            sum(ex.f_problem_sum) AS f_problem_sum,
            round(sum(round(((ex.f_get_point / (ex.f_equ_sum)::double precision))::numeric, 4)), 3) AS f_get_point
           FROM (t_levt_skill_expert ex
             JOIN ( SELECT sg_1.f_parent_pid,
                    sg_1.f_skill_id,
                    max((sg_1.f_last_modified_time)::text) AS f_last_modified_time
                   FROM t_levt_skill_expert sg_1
                  GROUP BY sg_1.f_parent_pid, sg_1.f_skill_id) ex1 ON (((((ex.f_parent_pid)::text = (ex1.f_parent_pid)::text) AND ((ex.f_skill_id)::text = (ex1.f_skill_id)::text)) AND ((ex.f_last_modified_time)::text = ex1.f_last_modified_time))))
          GROUP BY ex.f_parent_pid) sg ON (((t.f_pid)::text = (sg.f_parent_pid)::text)))
  WHERE ((1 = 1) AND (t.f_sys_flag = '1'::bpchar))
  GROUP BY t.f_user_id, t.f_levt_year, e.f_belong_org_id, e.f_yw_org_id, e.f_group_org_id, e.f_realname, e.f_sex, e.f_entry_time, e.f_post, sg.f_get_point, sg.f_problem_sum, sg.f_equ_sum, f.f_major_id, f.f_major_name, t2.f_levt_type, t2.f_substation_id, t2.f_voltage_classes
UNION ALL
 SELECT t1.f_pid AS f_user_id,
    t2.f_levt_year,
    t1.f_belong_org_id AS belong_org_id,
    t1.f_yw_org_id AS yw_org_id,
    t1.f_group_org_id AS org_id,
    t1.f_realname AS user_name,
        CASE
            WHEN (t1.f_sex = '1'::bpchar) THEN '男'::text
            ELSE '女'::text
        END AS user_sex_name,
    f_get_dic_name((t1.f_post)::text) AS post_name,
    floor((date_part('epoch'::text, (now() - to_timestamp('20170428'::text, 'yyyymmdd'::text))) / (((365 * 24) * 3600))::double precision)) AS work_years,
    t4.f_major_id AS major_id,
    t4.f_major_name AS major_name,
    t2.f_substation_id AS substation_id,
    t3.f_other_pid4 AS voltage_classes,
    t2.f_levt_type,
    COALESCE(t2.f_deduct_point, (0)::double precision) AS get_point,
    t2.f_levt_problem_sum AS problem_sum,
    0 AS equ_num,
    '1'::text AS is_yj
   FROM ((((t_levt_yj_user t
     LEFT JOIN t_user_info t1 ON ((((t.f_user_id)::text = (t1.f_pid)::text) AND (t1.f_sys_flag = '1'::bpchar))))
     LEFT JOIN t_levt_yj_task t2 ON ((((t.f_yj_task_pid)::text = (t2.f_pid)::text) AND (t2.f_sys_flag = '1'::bpchar))))
     LEFT JOIN t_equ_dic t3 ON ((((t2.f_substation_id)::text = (t3.f_pid)::text) AND (t3.f_sys_flag = '1'::bpchar))))
     LEFT JOIN t_user_small_major t4 ON ((((t.f_user_id)::text = (t4.f_user_id)::text) AND (t4.f_sys_flag = '1'::bpchar))))
  WHERE (t.f_sys_flag = '1'::bpchar);

