            ( SELECT string_agg((f_get_user_name((lte.f_user_id)::text))::text, ','::text ORDER BY lte.f_create_time) AS listagg
                   FROM t_levt_task_expert lte
                  WHERE (((lte.f_cp_equ_levt_task_pid)::text = (t.f_pid)::text) AND (lte.f_sys_flag = '1'::bpchar))) AS f_persons_name,
            ( SELECT string_agg((lte.f_user_id)::text, ','::text ORDER BY lte.f_create_time) AS listagg
                   FROM t_levt_task_expert lte
                  WHERE (((lte.f_cp_equ_levt_task_pid)::text = (t.f_pid)::text) AND (lte.f_sys_flag = '1'::bpchar))) AS persons_ids
           FROM t_levt_equ_task t
        UNION ALL
         SELECT ((t.f_pid)::text || 'yj'::text) AS f_pid,
            t.f_pid AS f_real_pid,
            t.f_province,
            t.f_city,
            t.f_branch,
            t.f_workteam,
            ''::character varying AS f_voltage_classes,
            t.f_substation_id,
            '1'::text AS f_is_yj,
            t.f_levt_type,
            t.f_levt_year,
            t.f_plan_begin_date,
            t.f_plan_end_date,
            t.f_work_task_name,
            t.f_work_task_info,
                CASE
                    WHEN (t.f_flow_task_name_result IS NULL) THEN t.f_flow_state
                    ELSE '1'::character varying
                END AS f_flow_state,
            t.f_create_org_id,
            t.f_sys_flag,
            t.f_creator,
            t.f_create_time,
            t.f_last_modifier,
            t.f_last_modified_time,
            t.f_levt_user AS f_users,
            ''::text AS f_equ_ids,
            ''::text AS f_equ_name,
            ( SELECT f_get_user_name((lyu.f_user_id)::text) AS f_get_user_name
                   FROM t_levt_yj_user lyu
                  WHERE (((lyu.f_yj_task_pid)::text = (t.f_pid)::text) AND (lyu.f_sys_flag = '1'::bpchar))) AS f_persons_name,
            ( SELECT lyu.f_user_id
                   FROM t_levt_yj_user lyu
                  WHERE (((lyu.f_yj_task_pid)::text = (t.f_pid)::text) AND (lyu.f_sys_flag = '1'::bpchar))) AS person_ids
           FROM (t_levt_yj_task t
             LEFT JOIN ( SELECT r.f_yj_task_pid,
                    (string_agg((r.f_user_id)::text, ','::text))::character varying AS yjuserid
                   FROM t_levt_yj_user r
                  WHERE (r.f_sys_flag = '1'::bpchar)
                  GROUP BY r.f_yj_task_pid) tr ON (((tr.f_yj_task_pid)::text = (t.f_pid)::text)))) levt_all
     LEFT JOIN t_equ_dic et ON ((((et.f_pid)::text = (levt_all.f_substation_id)::text) AND (et.f_sys_flag = '1'::bpchar))));

